function [x, e] = crosscorr (p);% Cross covariance of a set of tps.% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	[lx, n] = size(p);	x = zeros(lx, n-1+((n-2)^2+n-2)/2);	e = zeros (1, n);	c = rtp_to_dft (p);	ip = 1;	for i=1:n;		e(i) = sum(p(:,i).^2);		for k=i+1:n;			tc = c(:,i).*conj(c(:,k));			tp = dft_to_rtp (tc, lx);			x(:,ip) = irtp(tp);			ip = ip+1;		end;	end; 	x = x * sqrt(lx/2);  %%% KCM 9/20/06