function sig = mksig (x, t0, dt, type)% Constructor function for "sig"% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	if nargin==3; type=1; end;	if type==1;		sig = struct ('sig', x, 't0', t0, 'dt', dt);	elseif type==2;		t0 = t0 - floor (length(x)/2) * dt;		sig = struct ('sig', x, 't0', t0, 'dt', dt);	else		sig = x;	end;