function s2 = resamp (s1, dt2)% Resample s1 at a different sampling rate.% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	if dt2 >= s1.dt;		n = round (dt2/s1.dt);		s = s1.sig(1:n:end);	else		s = irtp (rtp(s1.sig), round(s1.dt/dt2));	end;	s2 = struct ('sig', s, 't0', s1.t0, 'dt', dt2);