function [sig, t0, dt, type] = sigsep (signal)% Extracts components of signal.% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	if isstruct (signal);		sig = signal.sig;		t0 = signal.t0;		dt = signal.dt;		type = 1;	elseif iscell (signal)		sig = signal{1};		t0 = signal{2};		dt = signal{3};		type = 1;	else		sig = signal;		t0 = 1;		dt = 1;		type = 0;	end;