% EMGlab installation nstructions.%% 1. Unzip the file EMGlab1.XX.zip. This zip file contains one folder named %	 'EMGlab1.XX' which contains the latest version of the EMGlab software.% 2. Move the EMGlab1.XX folder to whichever directory you want it to reside in.% 3. Run Matlab.% 4. In Matlab, select the EMGlab1.XX folder as the current directory. %	 You can do this using the Current Directory window, the cd command, %	 or the File Open menu.% 5. At the Matlab command prompt, type:%	  >> install% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net% Check the matlab version.if exist('try')~=5;	error ('EMGlab cannot run under this version of Matlab.');end;% Remove old versions of emglab from the matlab path.versions = which ('emglab', '-all');for i=1:length(versions);	directory = fileparts (versions{i});	if ~strcmp(directory, pwd)		rmpath (directory);%		rmpath (fullfile (directory, 'highres'));%		rmpath (fullfile (directory, 'readers'));%		rmpath (fullfile (directory, 'm files'));%		rmpath (fullfile (directory, 'sample data'));	end;end;% Add the current directory to the matlab path.addpath (pwd);% If there is already a preferences file in this directory, keep it.prefs_filename = 'emglab.prefs';if exist (fullfile (pwd, prefs_filename)) == 2;% Otherwise look for the most recent prefs file,else	most_recent_prefs_file = '';	most_recent_date = 0;	for i=1:length(versions);		p = fileparts (versions{i});		if ~strcmp (p, pwd);			file = fullfile(p, prefs_filename);			info = dir (file);			if ~isempty(info);				date = datenum(info.date);				if date > most_recent_date;					most_recent_prefs_file = file;					most_recent_date = date;				end;			end;		end;	end;% and copy it to the current directory.	if ~isempty (most_recent_prefs_file);		fsour = fopen (most_recent_prefs_file, 'r');		if fsour<=0;			warning ('Unable to copy preferences file.');		else			fdest = fopen (fullfile(pwd, prefs_filename), 'wt'); 			if fdest <=0;				warning ('Unable to copy preferences file');			else				while ~feof(fsour);					l = fgetl (fsour);					fprintf (fdest, '%s\n', l);				end;				fclose (fsour);				fclose (fdest);			end;		end;	end;end;			% Save the path;try 	stat = savepath;	if stat;		disp ('Please ask your system administrator to add')		disp (pwd)		disp ('to your Matlab path. Until then you will have')		disp ('to run emglab from this directory.')		return	end;catch	stat = path2rc;	if stat		disp ('Please select "Set Path" from the File menu');		disp ('  and click "Save" to complete the installation.');		return;	end;end;disp ('EMGLAB is now installed. To run it, type: emglab');