function c = cut (p, th);%	Cut dendogram%	p = dendogram from cluster.m%	th = cutting threshold%	c{i} = indices of elements in ith class% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	c = [];	p(p(:,3)>=th,:) = [];	i = 1;	while ~isempty(p);		x = p(end,1);		ip = 1;		while (ip<=length(x));			ix = find(p(:,1)==x(ip));			x = [x;p(ix,2)];			p(ix,:) = [];			ip = ip+1;		end;		c{i} = x;		i = i+ 1;	end;