function [m, s] = firingstats (times);% Compute firing statistics from a list of firing times.% Copyright (c) 2006-2009. Kevin C. McGill and others.% Part of EMGlab version 1.0.% This work is licensed under the Aladdin free public license.% For copying permissions see license.txt.% email: emglab@emglab.net	if length(times) < 4;		m = 0.1;		s = 0.02;		return;	end;	isi = diff(times);	for i=1:length(isi);		n(i) = sum(isi>0.67*isi(i) & isi<1.5*isi(i));	end;	k = find(n==max(n));	k = meaner (isi, k);	k = meaner (isi, k);	if length(k)>2;		m = mean(isi(k));		s = max(std(isi(k)), .0001);	else		m = 0.1;		s = 0.02;	end;	return;	function k = meaner (isi, k);	if isempty(k);	else		m = mean(isi(k));		k = find(isi>0.67*m & isi<1.5*m);	end;